import React, { useEffect, useState } from 'react';
import { useNavigate, Link as RouterLink } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Paper,
  Button,
  Avatar,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  CircularProgress
} from '@mui/material';
import { 
  Person as PersonIcon,
  Business as BusinessIcon,
  Security as SecurityIcon,
  Logout as LogoutIcon,
  PeopleAlt as PeopleIcon,
  AdminPanelSettings as AdminIcon,
  Inventory as InventoryIcon,
  ShoppingCart as OrdersIcon
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';

const DashboardPage: React.FC = () => {
  const { logout, userInfo } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(!userInfo);

  useEffect(() => {
    setLoading(!userInfo);
  }, [userInfo]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <Container sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Avatar sx={{ mr: 2, bgcolor: 'primary.main' }}>
              {userInfo?.username.charAt(0).toUpperCase()}
            </Avatar>
            <Box>
              <Typography variant="h5" component="h1">
                Welcome, {userInfo?.username}!
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {userInfo?.email}
              </Typography>
            </Box>
          </Box>
          <Button 
            variant="outlined" 
            startIcon={<LogoutIcon />}
            onClick={handleLogout}
          >
            Logout
          </Button>
        </Box>

        <Divider sx={{ my: 2 }} />

        <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 2 }}>
          <Paper sx={{ p: 2, flex: 1 }}>
            <Typography variant="h6" gutterBottom>
              User Information
            </Typography>
            <List dense>
              <ListItem>
                <ListItemIcon>
                  <PersonIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText 
                  primary="Username" 
                  secondary={userInfo?.username} 
                />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <BusinessIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText 
                  primary="Organization" 
                  secondary={userInfo?.tenantName} 
                />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <SecurityIcon fontSize="small" />
                </ListItemIcon>
                <ListItemText 
                  primary="Roles" 
                  secondary={userInfo?.roleNames.join(', ')} 
                />
              </ListItem>
            </List>
          </Paper>

          <Paper sx={{ p: 2, flex: 1 }}>
            <Typography variant="h6" gutterBottom>
              Quick Links
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
              {/* Admin Section - Only show for users with admin roles */}
              {userInfo?.roleNames.some(role => 
                ['SUPER_ADMIN', 'ADMIN'].includes(role)
              ) && (
                <>
                  <Typography variant="subtitle2" color="text.secondary" sx={{ mt: 1, mb: 1 }}>
                    Admin Functions
                  </Typography>
                  <Button 
                    variant="contained" 
                    fullWidth
                    component={RouterLink}
                    to="/admin/users"
                    startIcon={<PeopleIcon />}
                    sx={{ mb: 1 }}
                  >
                    User Management
                  </Button>
                  <Button 
                    variant="contained" 
                    fullWidth
                    component={RouterLink}
                    to="/admin/roles"
                    startIcon={<SecurityIcon />}
                  >
                    Role Management
                  </Button>
                </>
              )}

              <Typography variant="subtitle2" color="text.secondary" sx={{ mt: 1, mb: 1 }}>
                Store Operations
              </Typography>
              <Button 
                variant="contained" 
                fullWidth
                startIcon={<InventoryIcon />}
              >
                View Inventory
              </Button>
              <Button 
                variant="contained" 
                fullWidth
                startIcon={<OrdersIcon />}
              >
                Manage Orders
              </Button>
            </Box>
          </Paper>
        </Box>
      </Paper>

      <Typography variant="body2" color="text.secondary" align="center" sx={{ pt: 4 }}>
        Store Management System â€” {new Date().getFullYear()}
      </Typography>
    </Container>
  );
};

export default DashboardPage;
